mapper.select:
select*
from(
    select rownum as ROW_NUM, A.*, (sysdate-regdate)*24 as newImgTerm
    from(
        select*from board
        
	        where ${searchCondition} like '%'||#{searchKeyword}||'%'
        
        order by no desc
    )A
) where ROW_NUM>#{firstRecordIndex} and ROW_NUM<=#{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update board
set readcount=readcount+1
where no=#{no}

update board
set name=#{name},title=#{title},email=#{email},content=#{content} 
where no=#{no} and pwd=#{pwd}
